import React, {useEffect, useState, ChangeEvent} from 'react';
import './styles.css';
import logo from '../../assets/logo.svg';
import {Link} from 'react-router-dom';
import {FiArrowLeft} from 'react-icons/fi';
import {Map, TileLayer, Marker } from 'react-leaflet';
import {LeafletMouseEvent} from 'leaflet';
import api from '../../services/api';
import axios from 'axios';


//Estado para array e objeto deve ser informado o tipo da variável que será usada
interface Item {
    id: number,
    title: string;
    image_url: string;
};

interface IBGEUFResponse {
    sigla:string;
};

interface IBGECityResponse {
    nome:string;
};

const CreatePoint = () =>{
    const [items, setItems] = useState<Item[]>([]);
    const [ufs, setUfs] = useState<string[]>([]);
    const [cities, setCities] = useState<string[]>([]);
    //Guardar UF
    const [selectedUF, setSelectedUf] = useState('0');
    //Guardar Cidade
    const [selectedCity, setSelectedCity] = useState('0');
    //Guardar posição selecionada
    const [selectedPosition, setSelectedPosition] = useState<[number, number]>([0,0]);
    //Trás posição atual
    const [currentPosition, setCurrrentPosition] = useState<[number, number]>([0,0]);

    useEffect(() => {
        api.get('items').then(response => {
            setItems(response.data);
            //console.log(response.data);
        });
    }, []);

    useEffect(() => {
        axios.get<IBGEUFResponse[]>('https://servicodados.ibge.gov.br/api/v1/localidades/estados').then(response => {  
        const ufInitials = response.data.map(uf => uf.sigla) 
        setUfs(ufInitials);   
        });
    }, []);

    useEffect(() =>{
        //Verifica se UF está com valor padrão
        if (selectedUF === '0'){
            return;
        }
        //Necessário UF para carregar as cidades!
        axios.get<IBGECityResponse[]>(`https://servicodados.ibge.gov.br/api/v1/localidades/estados/${selectedUF}/municipios`).then(response => {  
        const cityNames = response.data.map(city => city.nome) 
        setCities(cityNames);   
        });
    
    },[selectedUF]);

    function handleSelectUf(event: ChangeEvent<HTMLSelectElement>){
        const uf = event.target.value;
        setSelectedUf(uf);
    }

    function handleSelectCity(event: ChangeEvent<HTMLSelectElement>){
        const city = event.target.value;
        setSelectedCity(city);
    }

    function handleMapClick(event: LeafletMouseEvent){
        setSelectedPosition([
            event.latlng.lat,
            event.latlng.lng
        ])
        console.log(event.latlng);
    };
    return (
    <div id="page-create-point">
        <header>
            <img src={logo} alt="Ecoleta"/>
            <Link to="/">
                <FiArrowLeft/>
                Voltar para home
            </Link>
        </header>
        <form action="">
            <h1>Cadastro do <br/> ponto de coleta</h1>
            <fieldset>
                <legend>
                    <h2>Dados</h2>
                </legend>
                <div className="field">
                    <label htmlFor="name">Nome da entidade:</label>
                    <input
                    type="text"
                    name="name"
                    id="name"
                    />
                </div>
                <div className="field-group">
                    <div className="field">
                        <label htmlFor="email">Email:</label>
                        <input
                        type="email"
                        name="email"
                        id="email"
                        />
                    </div>
                    <div className="field">
                        <label htmlFor="whatsapp">Whatsapp:</label>
                        <input
                        type="text"
                        name="whatsapp"
                        id="whatsapp"
                        />
                    </div>
                </div>
            </fieldset>
            <fieldset>
                <legend>
                    <h2>Endereço</h2>
                    <span>Selecione o endereço no mapa</span>
                </legend>
                <Map center={[-24.7021203 , -53.7260701]} zoom={15} onClick={handleMapClick}>
                    <TileLayer
                    attribution='&amp;copy <a href="https://osm.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={selectedPosition}/>
                </Map>

                <div className="field-group">
                    <div className="field">
                        <label htmlFor="uf">Estado (UF):</label>
                        <select onChange={handleSelectUf} 
                                value={selectedUF} 
                                name="uf" 
                                id="uf">

                            <option value="0">Selecione uma UF</option>
                            {ufs.map(uf => (
                            <option key={uf} value={uf}>{uf}</option> 
                            ))};
                        </select>
                    </div>
                    <div className="field">
                        <label htmlFor="city">Cidade:</label>
                        <select onChange={handleSelectCity} 
                                value={selectedCity} 
                                name="city" 
                                id="city">

                            <option value="0">Selecione uma Cidade</option>
                            {cities.map(city => (
                                <option key={city} value={city}>{city}</option>
                            ))};
                        </select>
                    </div>
                </div>
            </fieldset>
            <fieldset>
                <legend>
                    <h2>Itens de coleta</h2>
                    <span>Selecione um ou mais itens abaixo</span>
                </legend>
                <ul className="items-grid">
                   {items.map(item => (
                    <li key={item.id}>
                        <img src={item.image_url} alt={item.title}/>
                        <span>{item.title}</span>
                    </li>
                   ))};
                    
                </ul>
            </fieldset>
            <button type="submit">Cadastrar ponto de coleta</button>
        </form>
        
    </div>);
};

export default CreatePoint;
